INSERT INTO PUBLIC.POST (ID, SLUG, AUTHOR, TITLE, PEREX, BODY, PUBLISHED) VALUES (1, 'muj-skvely-den', 'Bara', 'Můj skvělý den', '<p>Ráno</p>', '<p>Ráno jsem se probudila čilá a plná života. Hned jsem vyskočila z postele a vyčistila jsem si zuby.</p>', '2021-05-28');
INSERT INTO PUBLIC.POST (ID, SLUG, AUTHOR, TITLE, PEREX, BODY, PUBLISHED) VALUES (2, 'muj-bezny-den', 'Bara', 'Můj běžný den', '<p>Ráno</p>', '<p>Ráno jsem se probudila nevyspalá a po zazvonění dvou budíků konečně vylezla z postele. Pomalu jsem se dobelhala do koupelny kde jsem si unaveně vyčistila zuby.</p>', '2021-05-27');
INSERT INTO PUBLIC.POST (ID, SLUG, AUTHOR, TITLE, PEREX, BODY, PUBLISHED) VALUES (3, 'muj-spatny-den', 'Bara', 'Můj špatný den', '<p>Ráno</p>', '<p>Ráno jsem se probudila a zjistila, že jsem zaspala. Na přípravu jsem měla asi deset minut. Vymrštila jsem se z postele a po cestě do koupelny omylem nakopla kočku. V koupelně jsem přeskočila čištění zubů, protože jsem nemohla najít kartáček.</p>', '2021-05-29');